import {
    Container,
    Text,
    Divider,
    Box,
    Image,
    Button,
    SimpleGrid,
    Flex,
} from "@chakra-ui/react";
import { FiShoppingCart, FiShoppingBag } from "react-icons/fi";
import { useState, useEffect } from "react";
import Butter from "buttercms";

const butter = Butter('0e8b572344e1d21e94fcf395c3546f7eba9ce8b4');

export default function Products() {
    const [products, setProducts] = useState([]);

    useEffect(() => {
        async function fetchData() {
            const res = await butter.content.retrieve(["project"], {
                order: "name",
            });
            const { data } = await res.data;
            const allProducts = data.project;
            setGoods(allGoods);
        }
        fetchData();
    }, []);


    return (
        <div className="products">
            <h1>Products Page</h1>
            {goods.map((goods) => (
                <Flex
                    mt="1"
                    justifyContent="space-between"
                    alignContent="center"
                >
                    <Text
                        fontSize="2xl"
                        fontWeight="semibold"
                        as="h4"
                        textTransform="uppercase"
                        lineHeight="tight"
                        fontFamily="Roboto"
                    >
                        {product.name}
                    </Text>
                    <Text
                        as="h4"
                        fontSize="2xl"
                        fontWeight="bold"
                        color="teal.600"
                    >
                        ${product.price}
                    </Text>
                </Flex>

            ))}

        </div>
    )
}
