import {
    Container,
    Text,
    Divider,
    Box,
    Image,
    Button,
    SimpleGrid,
    Flex,
} from "@chakra-ui/react";
import { FiShoppingCart, FiShoppingBag } from "react-icons/fi";
import { useState, useEffect } from "react";
import Butter from "buttercms";

const butter = Butter('0e8b572344e1d21e94fcf395c3546f7eba9ce8b4');

export default function Products() {
    const [goods, setGoods] = useState([]);

    useEffect(() => {
        async function fetchData() {
            const res = await butter.content.retrieve(["project"], {
                order: "name",
            });
            const { data } = await res.data;
            const allGoods = data.project;
            setGoods(allGoods);
        }
        fetchData();
    }, []);


    return (
        <div className="products">
            <h1>Products Page</h1>
            {goods.map((goods) => (
 <Box
 bg="white"
 maxW="sm"
 borderWidth="1px"
 rounded="lg"
 shadow="lg"
 _hover={{ shadow: "dark-lg" }}
 key={product.id}
>
 <Image
   h="350px"
   fit="cover"
   src={product.image}
   alt={`Picture of ${product.name}`}
   roundedTop="lg"
 />

 <Box p="6">
   <Flex
     mt="1"
     justifyContent="space-between"
     alignContent="center"
   >
     <Text
       fontSize="2xl"
       fontWeight="semibold"
       as="h4"
       textTransform="uppercase"
       lineHeight="tight"
       fontFamily="Roboto"
     >
       {product.name}
     </Text>
     <Text
       as="h4"
       fontSize="2xl"
       fontWeight="bold"
       color="teal.600"
     >
       ${product.price}
     </Text>
   </Flex>

   <Text
     mt={2}
     color="gray.500"
     display={{ base: "none", md: "flex" }}
   >
     {product.description}
   </Text>

   <Button
     leftIcon={<FiShoppingCart size="24px" />}
     size="lg"
     mt={4}
     isFullWidth
     colorScheme="blue"
     variant="outline"
     alignSelf={"center"}
     className="snipcart-add-item"
     data-item-id={product.id}
     data-item-image={product.image}
     data-item-name={product.name}
     data-item-url="/"
     data-item-description={product.description}
     data-item-price={product.price}
   >
     Add to Cart
</Button>
 </Box>
</Box>

            ))}

        </div>
    )
}
